import styled from "styled-components";

export const ContainerPrincipal = styled.div`
overflow-x: hidden;
width: 98vw;
`

export const ContainerMovie = styled.div`
display: flex;
margin-left: 100;
`

export const ContainerTitle = styled.div`
margin: 10px;


.ButtonL {
    position: absolute;
    left: 95vw
}

h2{
    margin-bottom: 15px;
}
`

export const ContainerTopCoverMovie = styled.div`
background-size: cover;
background-position: center;
background-image: linear-gradient( to right, black 100%, black 30%, black 100% );
width: 300px;

img{
    width: 100vw;
    opacity: 0.4;    
}

`
