import styled from "styled-components";

export const ContainerPrincipal = styled.div`
margin: 5px;


h2{
    margin-left: 10px;
    font-size: 2vw ;
  
}
`

export const ContainerTopCoverMovie = styled.div`
background-size: cover;
background-position: center;
background-image: linear-gradient( to right, black 30% );
width: 300px;

img{
    margin: 0px;
    padding: 0px;
    width: 99vw;
    opacity: 0.4;    
}

`
