import React from 'react'
import { BASE_URL, KEY_API } from '../../constantes/urls'
import useRequestData from '../../hooks/useRequestData'
import MoviesOriginaisNetflix from '../../components/MoviesOriginaisNetfliex'
import MoviesRecomended from '../../components/MoviesRecomended'
import MoviesTop from '../../components/MoviesTop'
import MoviesAction from '../../components/MoviesAction'
import MoviesComedy from '../../components/MoviesComedy'
import { ContainerPrincipal, ContainerMovie, ContainerTitle } from './styled'


const HomePage = () => {

    const coverMovie = useRequestData ([])

    const originais = useRequestData ([], 
        `${BASE_URL}/discover/tv?with_network=213&language=pt-BR&api_key=${KEY_API}`
    ) 
    console.log (originais)

    const recomended = useRequestData ([], 
        `${BASE_URL}/trending/all/week?language=pt-BR&api_key=${KEY_API}`
    )

    const top = useRequestData ([], 
        `${BASE_URL}/movie/top_rated?language=pt-BR&api_key=${KEY_API}`
    )
 
    const action = useRequestData ([], 
        `${BASE_URL}/discover/movie?with_genres=28&language=pt-BR&api_key=${KEY_API}`
    )
  
    const comedy = useRequestData ([], 
        `${BASE_URL}/discover/movie?with_genres=35&language=pt-BR&api_key=${KEY_API}`
    )
  
    const originaisNet = originais.map ((index => {
        return <MoviesOriginaisNetflix  key={index.id} index={index}/>
    })) 

    const recomendedNet = recomended.map ((index => {
        return <MoviesRecomended key={index.id} index={index}/>
    }))

    const topNet = top.map ((index => {
        return <MoviesTop key={index.id} index={index}/>
    }))

    const topAction = action.map ((index => {
        return <MoviesAction key={index.id} index={index}/>
    }))

    const topComedy = comedy.map ((index => {
        return <MoviesComedy key={index.id} index={index}/>
    }))

    return (

        <ContainerPrincipal>

            <ContainerTitle>
                <h2>Originais Netflix</h2>
            </ContainerTitle>
            <ContainerMovie>
                {originaisNet}
            </ContainerMovie>

            <ContainerTitle>
                <h2>Recomendados para Você</h2>
            </ContainerTitle>
            <ContainerMovie>
                {recomendedNet}
            </ContainerMovie>

            <ContainerTitle>
                <h2>Em Alta</h2>
            </ContainerTitle>
            <ContainerMovie>
                {topNet}
            </ContainerMovie>

            <ContainerTitle>
                <h2>Ação</h2>
            </ContainerTitle>
            <ContainerMovie>
                {topAction}
            </ContainerMovie>
            
            <ContainerTitle>
                <h2>Comédia</h2>
            </ContainerTitle>
            <ContainerMovie>
                {topComedy}
            </ContainerMovie>                
               
        </ContainerPrincipal>
    )
}

export default HomePage
