import styled from 'styled-components'

export const ContainerMoviePrincipal = styled.div`
margin: 5px;
display: inline-block;

`

export const ContainerMovie = styled.div`


img {
    width: 150px;
    transform: scale(0.9);
    transition: ease;

}

img:hover {
    transform: scale(1);
}

`
