import { Button } from '@material-ui/core'
import React, {useState} from 'react'
import { ContainerPrincial, ContainerTitle, ContainerButton} from './styled'
import { useHistory} from 'react-router-dom'
import { goToHome } from '../../routers/cordinator'
import Comments from '../../components/Comments/Comments'

const MoviePage = () => {





    const onChangeComment = (event) => {
        setComment (event.target.value)
    }

    const onChangeName = (event) => {
        setName (event.target.value)
    }

    const sendComment = () => {
        const newComment = {
            name: name,
            comment: comment
        }
        const newList = [newComment, ...listComment]
        setListCommet (newList)
        setComment ("")
        setName ("")
    }

    const showListComment = listComment.map ((index => {
        return <Comments key={index.name} index={index}/>
    }))

    const history = useHistory()

    const clickBack = () => {
        goToHome(history)
    }

    return (

        <div>
            
        <ContainerTitle>
            <h1>Gostou dos títulos?</h1> 
            <p>Deixe um comentário!</p>
        </ContainerTitle>

        {showListComment}

        <ContainerPrincial>
            
            <input placeholder="Nome" onChange={onChangeName} value={name} />
            <input placeholder="Comentário" onChange={onChangeComment} value={comment} />

            <ContainerButton>
                <Button onClick={sendComment}
                    variant="contained" 
                    color="primary">
                    Enviar
                </Button>       
            </ContainerButton>

            <Button
                onClick={clickBack}
                variant="contained" 
                color="primary">
                Voltar
            </Button>  

        </ContainerPrincial>
        
        </div>
    )
}

export default MoviePage
