import { Button } from '@material-ui/core'
import React, {useState} from 'react'
import { ContainerPrincial, ContainerTitle, ContainerButton} from './styled'
import { useHistory} from 'react-router-dom'
import { goToHome } from '../../routers/cordinator'

const MoviePage = () => {

    const history = useHistory()

    const clickBack = () => {
        goToHome(history)
    }

    const [comment, setComment] = useState("")

    const [listComment, setListCommet] = useState([
        {comment: "Adorei os titulos"},
        {comment: "Execlentes!!"}
    ])

    const onChangeComment = (event) => {
        setComment(event.target.value)
    }

    
    const sendComment = () => {
        const newComment = {
            comment: comment
        }
        const newList = [newComment, ...listComment]
        setListCommet (newList)
        setComment ("")
    }

    console.log (listComment)


    return (
        <div>

        <ContainerTitle>
            <h1>Gostou dos títulos?</h1> 
            <p>Deixe um comentário!</p>
        </ContainerTitle>
        
        <ContainerPrincial>

            <input onChange={onChangeComment} value={comment} />

            <ContainerButton>
                <Button onClick={sendComment}
                    variant="contained" 
                    color="primary">
                    Enviar
                </Button>       
            </ContainerButton>

            <Button
                onClick={clickBack}
                variant="contained" 
                color="primary">
                Voltar
            </Button>  

        </ContainerPrincial>
        </div>
    )
}

export default MoviePage
