import axios from 'axios'
import { useState } from 'react'
import { BASE_URL, KEY_API } from '../constantes/urls'

export const req_originaisNetflix = () => {

    axios.get(`${BASE_URL}/discover/tv?with_network=213&language=pt-BR&api_key=${KEY_API}`)

    .then ((res) => {
      console.log (res.data)
      setOriginaisNetflix (res.data)
    })
    .catch ((err) => {
      console.log (err.data)
    })
}

export const [originaisNetiflix, setOriginaisNetflix] = useState([])




export const recomendados = () => {

    axios.get(`${BASE_URL}/trending/all/week?language=pt-BR&api_key=${KEY_API}`)

    .then ((res) => {
      console.log (res.data)
    })
    .catch ((err) => {
      console.log (err.data)
    })
}

export const emAlta = () => {

    axios.get(`${BASE_URL}/top_rated?language=pt-BR&api_key=${KEY_API}`)

    .then ((res) => {
      console.log (res.data)
    })
    .catch ((err) => {
      console.log (err.data)
    })
}
