import React from 'react'
import useRequestData from '../../hooks/useRequestData'
import { BASE_URL, KEY_API } from '../../constantes/urls'
import MoviesOriginaisNetflix from '../../components/MoviesOriginaisNetfliex'
import MoviesRecomended from '../../components/MoviesRecomended'
import { ContainerPrincipal, ContainerMovie, Movie } from './styled'


const HomePage = () => {

    const originais = useRequestData ([], `${BASE_URL}/discover/tv?with_network=213&language=pt-BR&api_key=${KEY_API}`)
  
    const recomended = useRequestData ([], `${BASE_URL}/trending/all/week?language=pt-BR&api_key=${KEY_API}`)

    const top = useRequestData ([], `${BASE_URL}/movie/top_rated?language=pt-BR&api_key=${KEY_API}`)
 
    const action = useRequestData ([], `${BASE_URL}/discover/movie?with_genres=28&language=pt-BR&api_key=${KEY_API}`)
  
    const comedy = useRequestData ([], `${BASE_URL}/discover/movie?with_genres=35&language=pt-BR&api_key=${KEY_API}`)
  
    const originaisNet = originais.map ((index => {
     return <MoviesOriginaisNetflix  key={index.id} index={index}/>
    })) 

    const recomendedNet = recomended.map ((index => {
        return <MoviesRecomended key={index.id} index={index}/>
    }))

    return (

        <ContainerPrincipal>
            
            <h2>Originais Netflix</h2>
                <ContainerMovie>
                    {originaisNet}
                </ContainerMovie>

            <h2>Originais Netflix</h2>
                <ContainerMovie>
                    {recomendedNet}
                </ContainerMovie>
            

        </ContainerPrincipal>
    )
}

export default HomePage
