import axios from 'axios'
import { useState } from 'react'
import { BASE_URL, KEY_API } from '../constantes/urls'

export const originaisNetflix = () => {

    axios.get(`${BASE_URL}/discover/tv?with_network=213&language=pt-BR&api_key=${KEY_API}`)

    .then ((res) => {
      console.log (res.data)
      setOri (res.data)
    })
    .catch ((err) => {
      console.log (err.data)
    })
}

