import axios from 'axios'
import { BASE_URL2 } from '../constantes/urls'
import { goToHome } from '../routers/cordinator'
import { BASE_URL, KEY_API } from "../constantes/urls"
import useRequestData from '../hooks/useRequestData'


    export const coverMovie = useRequestData ([], 
        `${BASE_URL}/discover/tv?with_network=213&language=pt-BR&api_key=${KEY_API}`
    )
    
    export const originais = useRequestData ([], 
        `${BASE_URL}/discover/tv?with_network=213&language=pt-BR&api_key=${KEY_API}`
    ) 

    export const recomended = useRequestData ([], 
        `${BASE_URL}/trending/all/week?language=pt-BR&api_key=${KEY_API}`
    )

    export const top = useRequestData ([], 
        `${BASE_URL}/movie/top_rated?language=pt-BR&api_key=${KEY_API}`
    )



export default Requests

export const login = (body, clear, history, setIsLoading) => {
    setIsLoading(true)
    axios.post (`${BASE_URL2}/user/login`, body)
    .then ((res) => {
        localStorage.setItem("token", res.data.token)
        clear()
        goToHome(history)
        setIsLoading()
    })
    .catch ((err) => {
        setIsLoading(false)
        alert (err.response.data.message)
    })
}

export const signup = (body, clear, history, setIsLoading) => {
    setIsLoading(true)
    axios.post (`${BASE_URL2}/user/signup`, body)
    .then ((res) => {
        localStorage.setItem("token", res.data.token)
        clear()
        goToHome(history)
    })
    .catch ((err) => {
        setIsLoading(false)
        alert (err.response.data.message)
    })
}
