import axios from 'axios'
import { BASE_URL2 } from '../constantes/urls'

export const login = (body, clear, history) => {

    axios.post (`${BASE_URL2}/user/login`, body)
    .then ((res) => {
        localStorage.setItem("token", res.data.token)
        clear()
    })
    .catch ((err) => {
        alert ("Erro no Login")
    })
}
