import styled from "styled-components";

export const Container = styled.div`
margin: 5px;
padding: 0px;
`

export const ContainerTitle = styled.div`
margin: 0px;
padding: 0px;
width: 250px;
position: absolute;
top: 30vw;
left: 15px;

h1 {
    font-size: 3vw;
}
`
