import styled from "styled-components";

export const ContainerPrincial = styled.div`
display: flex;
flex-direction: column;
justify-content: center;
width: 0vw;
margin: 20px auto;
padding: 18px;
background-color: #222;
border-radius: 8px;
border-left: solid 3px red;

p {
    padding: 5px;
}
`
