import styled from 'styled-components'

export const ContainerMoviePrincipal = styled.div`
margin: 4px;
display: inline-block;
`

export const ContainerMovie = styled.div`

img {
    width: 10vw;
    transform: scale(0.9);
    transition: ease 0.2s;
    cursor: pointer;
   
}

img:hover {
    transform: scale(1);
}
`
