import React from 'react'
import { Button } from '@material-ui/core'
import { ContainerPrincial, ContainerInput } from './styled'
import useForm from '../../hooks/useForm'

const SignupPage = () => {

    const [form, onChange, clear] = useForm({email: "", password: ""})

    const onSubmitForm = (event) => {
        event.preventDefault()
        console.log (form)
    }

    return (

        <ContainerPrincial>
            <h1>Cadastre-se</h1>

            <ContainerInput>
                <form onSubmit={onSubmitForm}>
                <input 
                        placeholder="E-mail"
                        name={"email"}
                        value={form.email}
                        onChange={onChange}
                        required
                        type={"email"}
                    />
                    <input 
                        placeholder="E-mail"
                        name={"email"}
                        value={form.email}
                        onChange={onChange}
                        required
                        type={"email"}
                    />

                    <input 
                        placeholder="Senha"
                        name={"password"}
                        value={form.password}
                        onChange={onChange} 
                        required
                        type={"password"}
                    />

                    <Button 
                        type={"submit"}
                        variant="contained" 
                        color="primary">
                        Entrar
                    </Button>
                </form>
            </ContainerInput>
        </ContainerPrincial>
    )
}

export default SignupPage
